def Add(Num1,Num2):
    return Num1 + Num2

def Sub(Num1,Num2):
    return Num1 - Num2


def Mutiply(Num1,Num2):
    return Num1 * Num2

def Div(Num1,Num2):
    return Num1 / Num2

def Big_add(Num1,Num2,Num3,Num4):
    return Num1 + Num2 + Num3 + Num4

def Big_sub(Num1,Num2,Num3,Num4):
    return Num1 - Num2 - Num3 - Num4

def Big_mutiply(Num1,Num2,Num3,Num4):
    return Num1 * Num2 * Num3 * Num4

def Big_div(Num1,Num2,Num3,Num4):
    return Num1 / Num2 / Num3 / Num4
