mxpiblock.block:
Blockly.Blocks.MQTT_msg= {
init: function() {
this.appendDummyInput()
    .appendField("中的MQTT消息");
this.setInputsInline(true);
this.setOutput(true, null);
this.setColour(90);
this.setTooltip("");
this.setHelpUrl("");
}
};

mxpiblock.code:
Blockly.Python.MQTT_msg = function() {
Blockly.Python.definitions_['time'] = 'import time';
var code = 'time.time()';
return [code, Blockly.Python.ORDER_ATOMIC];
};
